function SetDeleteModalValue(n,t){$("#delete-modal .source-delete").text(n);$("#delete-modal .value-delete").val(t)}$.fn.remove=function(){$(this).click(function(){var n=$("#delete-modal .value-delete").val();$.ajax({type:"post",url:project_delete_url,data:{code:n},success:function(){window.location="/project.html"},error:function(){$("#delete-modal .error-message").text("Error, EzTask cannot execute deleting data. Try again please !")}})})};$.fn.DeleteConfirm=function(){$(this).click(function(){var n=$(this).attr("data-source"),t=$(this).attr("data-code");SetDeleteModalValue("project: "+n,t);ShowModal("delete-modal")})};$.fn.CancelDelete=function(){$(this).click(function(){SetDeleteModalValue("","");CloseModal("delete-modal")})};$(function(){$(".remove-project").DeleteConfirm();$("#delete-modal .btn-delete").remove();$("#delete-modal .close").CancelDelete();ModalCloseTrigger("delete-modal")});