function setPhraseModalValue(n,t){$("#inputPhraseName").val("");$("#phrase-modal .project-id").val(n);$("#phrase-modal .phrase-id").val(t)}function formatDate(n){var t=new Date(n);return t.getMonth()+1+"/"+t.getDate()+"/"+t.getFullYear()}$.fn.showModal=function(){$(this).click(function(){var n=$(".project-list").val();setPhraseModalValue(n,0);$.showDialog({dialogId:"phrase-modal",title:"Add new phrase"})})};$.fn.phraseModalAction=function(){$(this).click(function(n){n.preventDefault();var t=$("#phrase-form"),i=$(".project-list").val(),r=$(".phrase-id").val();t.valid()&&$.ajax({type:"post",url:"task/phrase-modal-action.html",data:t.serialize(),success:function(n){var t=$(".phrase-list-panel");t.html("");t.html(n);$("#inputPhraseName").val("");$("#inputStartDate").val(formatDate(n.startDate));$("#inputEndDate").val(formatDate(n.endDate));$.closeDialog("phrase-modal")},error:function(){$("#phrase-modal .error-message").text("Error, EzTask cannot execute deleting data. Try again please !")}})})};$(function(){$(".btn-addnew-phrase").showModal();$("#phrase-modal .btn-confirm").phraseModalAction();$.triggerCloseDialog("phrase-modal")});