function readURL(n){if(n.target.files&&n.target.files[0]){var t=new FileReader;t.onload=function(n){$(".img-avatar").attr("src",n.target.result)};t.readAsDataURL(n.target.files[0])}}$.fn.uploadAvatar=function(){$(this).click(function(){var n=new FormData,t=$("#image-upload")[0],i=t.files[0];n.append("file",i);$.ajax({type:"post",url:"upload-avatar.html",data:n,processData:!1,contentType:!1,success:function(){window.location="profile.html"},error:function(n){$("#upload-avatar-modal .error-message").text(n.responseText)}})})};$(function(){$("#image-upload").change(readURL);$(".btn-upload-avatar").uploadAvatar()});