$.fn.deleteNotifyItem=function(){$(this).click(function(){$.showLoading();var n=$(this).data("notifyid");$.ajax({url:"/notification/delete.html",type:"POST",data:{notifyId:n},success:function(){$.loadNotifyList()}})})};$.loadNotifyList=function(){$.ajax({url:"/notification/load-list.html",type:"POST",success:function(n){$(".notification-box-content").html("");$(".notification-box-content").html(n);$(".btn-delete").deleteNotifyItem();$.hideLoading()}})};$(function(){$.showLoading();$.loadNotifyList()});