function formatDate(n){var t=new Date(n);return t.getMonth()+1+"/"+t.getDate()+"/"+t.getFullYear()}$.fn.showModal=function(){$(this).click(function(){var n=0,t=$(".project-list").val();$(this).hasClass("edit-phrase")&&(n=$("#phrase-id").val());$.showLoading();$.ajax({url:"task/generate-phrase.html",type:"POST",data:{phraseId:n,projectId:t},success:function(n){$(".phrase-template").html(n);$.initCommonLib();$("#phrase-modal .btn-confirm").phraseModalAction();$.hideLoading();$.showDialog({dialogId:"phrase-modal"});$.triggerCloseDialog("phrase-modal")}})})};$.fn.phraseModalAction=function(){$(this).click(function(n){n.preventDefault();var t=$("#phrase-form");$.showLoading();t.valid()&&$.ajax({type:"post",url:"task/phrase-modal-action.html",data:t.serialize(),success:function(n){var t=$(".phrase-list-panel");t.html("");t.html(n)},error:function(n){$.alertDialog({title:"Error",content:n.responseText})}}).promise().done(function(){$.closeDialog("phrase-modal");$(".phrase-list > li > a").loadTask();$.hideLoading()})})};$.fn.removePhrase=function(){$(this).click(function(){$.confirmDialog({title:"Warning",content:"All tasks which related to this phrase will be removed. Are you sure to continue?",action:function(){$.showLoading();$.ajax({type:"post",url:"task/delete-phrase.html",data:{phraseId:$("#phrase-id").val()},success:function(){$(".project-list").change();$.hideLoading()},error:function(n){$.hideLoading();$.alertDialog({title:"Error",content:n.responseText})}})}})})};$(function(){$(".btn-addnew-phrase").showModal()});