function setPhraseModalValue(n,t){var i=new Date;$("#inputPhraseName").val("");$("#phrase-modal .project-id").val(n);$("#phrase-modal .phrase-id").val(t);$("#phrase-modal #inputStartDate").val(i.getDate()+"/"+(i.getMonth()+1)+"/"+i.getFullYear());$("#phrase-modal #inputEndDate").val(i.getDate()+1+"/"+(i.getMonth()+1)+"/"+i.getFullYear())}function formatDate(n){var t=new Date(n);return t.getMonth()+1+"/"+t.getDate()+"/"+t.getFullYear()}$.fn.showModal=function(){$(this).click(function(){var n=$(".project-list").val();setPhraseModalValue(n,0);$.showDialog({dialogId:"phrase-modal",title:"Add new phrase"})})};$.fn.phraseModalAction=function(){$(this).click(function(n){n.preventDefault();var t=$("#phrase-form");t.valid()&&$.ajax({type:"post",url:"task/phrase-modal-action.html",data:t.serialize(),success:function(n){var t=$(".phrase-list-panel");t.html("");t.html(n);$(".phrase-list > li > a").loadTask();$.closeDialog("phrase-modal")},error:function(){$("#phrase-modal .error-message").text("Error, EzTask cannot create phrase. Try again please !")}})})};$(function(){$(".btn-addnew-phrase").showModal();$("#phrase-modal .btn-confirm").phraseModalAction();$.triggerCloseDialog("phrase-modal")});