function SetPhraseModalValue(n,t){$("#inputPhraseName").val("");$("#phrase-modal .project-id").val(n);$("#phrase-modal .phrase-id").val(t)}function FormatDate(n){var t=new Date(n);return t.getMonth()+1+"/"+t.getDate()+"/"+t.getFullYear()}$.fn.ShowModal=function(){$(this).click(function(){var n=$(".project-list").val();SetPhraseModalValue(n,0);$.showDialog("phrase-modal","Add new phrase")})};$.fn.PhraseModalAction=function(){$(this).click(function(n){n.preventDefault();var t=$("#phrase-form"),i=$(".project-list").val(),r=$(".phrase-id").val();t.valid()&&$.ajax({type:"post",url:"task/phrase-modal-action.html",data:t.serialize(),success:function(n){var t=$(".phrase-list-panel");t.html("");t.html(n);$("#inputPhraseName").val("");$("#inputStartDate").val(FormatDate(n.startDate));$("#inputEndDate").val(FormatDate(n.endDate));$.closeDialog("phrase-modal")},error:function(){$("#phrase-modal .error-message").text("Error, EzTask cannot execute deleting data. Try again please !")}})})};$(function(){$(".btn-addnew-phrase").ShowModal();$("#phrase-modal .btn-confirm").PhraseModalAction();$.triggerCloseDialog("phrase-modal")});